import React from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";

function Layout() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-white shadow-lg flex flex-col">
        <div className="p-4 font-bold text-xl border-b">StudyBuddy</div>
        <nav className="flex-1 p-4 space-y-2">
          <Link to="/" className="block p-2 hover:bg-gray-200 rounded">üìä Dashboard</Link>
          <Link to="/upload" className="block p-2 hover:bg-gray-200 rounded">‚¨ÜÔ∏è Upload</Link>
          <Link to="/files" className="block p-2 hover:bg-gray-200 rounded">üìÇ Files</Link>
          <Link to="/subjects" className="block p-2 hover:bg-gray-200 rounded">üìò Subjects</Link>
          <Link to="/profile" className="block p-2 hover:bg-gray-200 rounded">üë§ Profile</Link>
        </nav>
        <button onClick={logout} className="m-4 bg-red-500 text-white py-2 rounded">
          Logout
        </button>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Header */}
        <header className="flex justify-between items-center p-4 bg-white shadow">
          <h1 className="text-xl font-semibold">Welcome to StudyBuddy</h1>
          <div className="font-medium">{user?.name || "User"}</div>
        </header>

        {/* Page Content */}
        <main className="flex-1 p-6 overflow-auto">
          <Outlet />
        </main>
      </div>
    </div>
  );
}

export default Layout;
