import React from "react";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const navigate = useNavigate();

  const cards = [
    { title: "Upload Notes", desc: "Upload and manage your study materials", icon: "‚¨ÜÔ∏è", path: "/upload" },
    { title: "View Files", desc: "Access uploaded PDFs and files", icon: "üìÅ", path: "/files" },
    { title: "Subjects", desc: "Organize your subjects and topics", icon: "üìò", path: "/subjects" },
  ];

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-semibold text-slate-800">
          Welcome to <span className="text-blue-600">StudyBuddy</span>
        </h1>
        <p className="text-gray-500 text-sm">Logged in as <b>Sk Yasmin</b></p>
      </div>

      {/* Quick Links */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-6">
        {cards.map((item) => (
          <div
            key={item.title}
            onClick={() => navigate(item.path)}
            className="bg-white shadow-md hover:shadow-xl transition rounded-2xl border border-slate-200 cursor-pointer p-6 text-center"
          >
            <div className="text-3xl mb-3">{item.icon}</div>
            <h3 className="font-semibold text-slate-700">{item.title}</h3>
            <p className="text-gray-500 text-sm mt-2">{item.desc}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
