import React from "react";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const navigate = useNavigate();

  const cards = [
    {
      title: "Upload Notes",
      desc: "Upload and manage your study materials",
      icon: "‚¨ÜÔ∏è",
      path: "/dashboard/upload",
    },
    {
      title: "View Files",
      desc: "Access uploaded PDFs and files",
      icon: "üìÅ",
      path: "/dashboard/files",
    },
    {
      title: "Subjects",
      desc: "Organize your subjects and topics",
      icon: "üìò",
      path: "/dashboard/subjects",
    },
  ];

  const studyStats = [
    { subject: "Python", progress: 20, color: "bg-blue-500" },
    { subject: "Java", progress: 0, color: "bg-green-500" },
    { subject: "JavaSCript", progress: 80, color: "bg-yellow-500" },
  ];

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-semibold text-slate-800">
          Welcome to <span className="text-blue-600">StudyBuddy</span>
        </h1>
        <p className="text-gray-500 text-sm">Logged in as <b>Sk Yasmin</b></p>
      </div>

      {/* Two main cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Study Progress */}
        <div className="bg-white shadow-lg rounded-2xl border border-slate-200 p-6">
          <h2 className="text-lg font-semibold text-slate-700 mb-4">Study Progress</h2>
          <div className="space-y-4">
            {studyStats.map((stat) => (
              <div key={stat.subject}>
                <div className="flex justify-between mb-1">
                  <span className="text-gray-700 font-medium">{stat.subject}</span>
                  <span className="text-gray-500 text-sm">{stat.progress}%</span>
                </div>
                <div className="w-full bg-slate-200 rounded-full h-4">
                  <div
                    className={`${stat.color} h-4 rounded-full`}
                    style={{ width: `${stat.progress}%` }}
                  ></div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Announcements */}
        <div className="bg-white shadow-lg rounded-2xl border border-slate-200 p-6">
          <h2 className="text-lg font-semibold text-slate-700 mb-4">Announcements</h2>
          <ul className="list-disc list-inside text-gray-600 space-y-2">
            <li>üìÖ Group meeting at 5 PM</li>
            <li>üìù Submit assignment by Friday</li>
          </ul>
        </div>
      </div>

      {/* Quick Links */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-6">
        {cards.map((item) => (
          <div
            key={item.title}
            onClick={() => navigate(item.path)}
            className="bg-white shadow-md hover:shadow-xl transition rounded-2xl border border-slate-200 cursor-pointer p-6 text-center"
          >
            <div className="text-3xl mb-3">{item.icon}</div>
            <h3 className="font-semibold text-slate-700">{item.title}</h3>
            <p className="text-gray-500 text-sm mt-2">{item.desc}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
