import React, { useState, useEffect } from "react";
import axios from "axios";

function Upload() {
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [subject, setSubject] = useState("");
  const [files, setFiles] = useState([]);

  const token = localStorage.getItem("token");

  // ✅ Fetch files for logged-in user
  const fetchFiles = async () => {
    try {
      const res = await axios.get("http://localhost:5000/upload", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFiles(res.data);
    } catch (err) {
      console.error("Error fetching files:", err);
    }
  };

  useEffect(() => {
    fetchFiles();
  }, []);

  // ✅ Handle file upload
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      alert("Please select a PDF file!");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("title", title);
    formData.append("subject", subject);

    try {
      await axios.post("http://localhost:5000/upload", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });

      alert("File uploaded successfully!");
      setFile(null);
      setTitle("");
      setSubject("");
      fetchFiles(); // refresh list
    } catch (err) {
      console.error("Upload error:", err);
      alert(err.response?.data?.message || "Failed to upload file");
    }
  };

  return (
    <div className="bg-white shadow rounded p-6 max-w-3xl mx-auto mt-10">
      <h2 className="text-xl font-semibold mb-4">Upload PDF</h2>

      <form onSubmit={handleUpload} className="space-y-4">
        <input
          type="file"
          accept="application/pdf"
          onChange={(e) => setFile(e.target.files[0])}
        />

        <input
          type="text"
          placeholder="Title"
          className="border rounded p-2 w-full"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />

        <select
          className="border rounded p-2 w-full"
          value={subject}
          onChange={(e) => setSubject(e.target.value)}
        >
          <option value="">Select Subject</option>
          <option value="Math">Math</option>
          <option value="Physics">Physics</option>
          <option value="Chemistry">Chemistry</option>
        </select>

        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Upload
        </button>
      </form>

      <h3 className="mt-6 font-semibold">Uploaded Files</h3>
      <div className="mt-3 grid grid-cols-1 md:grid-cols-2 gap-4">
        {files.map((f) => (
          <div key={f.id} className="p-3 border rounded shadow-sm">
            <p className="font-medium">{f.title}</p>
            <p className="text-sm text-gray-500">{f.subject}</p>
            <a
              href={`http://localhost:5000/uploads/${f.filename}`}
              download
              className="text-blue-500 mt-2 block"
            >
              Download
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Upload;
